const CONFIG_FILE = path.join(process.cwd(), 'rest.config');
if (fs.existsSync(CONFIG_FILE)) {
    let config = fs.readJSONSync(CONFIG_FILE);
    if (config.useIoC) {
        Server.useIoC();
    } else if (config.serviceFactory) {
        if (config.serviceFactory.indexOf('.') === 0) {
