}

"use strict";

import {HttpError} from "./server-types"; 

/**
 * Represents a BAD REQUEST error. The request could not be understood by the 
 * server due to malformed syntax. The client SHOULD NOT repeat the request 
 * without modifications.
 */
export class BadRequestError extends HttpError {
	constructor(message?: string) {
		super("BadRequestError", 400, message || "Bad Request");
		Object["setPrototypeOf"](this, BadRequestError.prototype);
	}
}

/**
 * Represents an UNAUTHORIZED error. The request requires user authentication. The response 
 * MUST include a WWW-Authenticate header field containing a challenge applicable to the 
 * requested resource. 
 */
export class UnauthorizedError extends HttpError {
	constructor(message?: string) {
		super("UnauthorizedError", 401, message || "Unauthorized");
		Object["setPrototypeOf"](this, UnauthorizedError.prototype);
	}
}

/**
 * Represents a FORBIDEN error. The server understood the request, but is refusing to 
 * fulfill it. Authorization will not help and the request SHOULD NOT be repeated.
 */
export class ForbidenError extends HttpError {
	constructor(message?: string) {
		super("ForbidenError", 403, message || "Forbiden");
		// Object.setPrototypeOf(this, ForbidenError.prototype);
        // this["__proto__"] = ForbidenError.prototype;
		Object["setPrototypeOf"](this, ForbidenError.prototype);
	}
}

/**
 * Represents a NOT FOUND error. The server has not found anything matching 
 * the Request-URI. No indication is given of whether the condition is temporary 
 * or permanent. The 410 (GoneError) status code SHOULD be used if the server knows, 
 * through some internally configurable mechanism, that an old resource is permanently 
 * unavailable and has no forwarding address. 
 * 
 * This error is commonly used when 
 * the server does not wish to reveal exactly why the request has been refused, 
 * or when no other response is applicable.
 */
export class NotFoundError extends HttpError {
	constructor(message?: string) {
		super("NotFoundError", 404, message || "Not Found");
		Object["setPrototypeOf"](this, NotFoundError.prototype);
	}
}

/**
 * Represents a METHOD NOT ALLOWED error. The method specified in the Request-Line is not allowed for 
 * the resource identified by the Request-URI. The response MUST include an Allow header 
 * containing a list of valid methods for the requested resource.
 */
export class MethodNotAllowedError extends HttpError {
	constructor(message?: string) {
		super("MethodNotAllowedError", 405, message || "Method Not Allowed");
		Object["setPrototypeOf"](this, MethodNotAllowedError.prototype);
	}
}

/**
 * Represents a NOT ACCEPTABLE error. The resource identified by the request is only capable of 
 * generating response entities which have content characteristics not acceptable according 
 * to the accept headers sent in the request.
 */
export class NotAcceptableError extends HttpError {
	constructor(message?: string) {
		super("NotAcceptableError", 406, message || "Not Acceptable");
		Object["setPrototypeOf"](this, NotAcceptableError.prototype);
	}
}
/**
 * Represents a CONFLICT error. The request could not be completed due to a 
 * conflict with the current state of the resource.
 */
export class ConflictError extends HttpError {
	constructor(message?: string) {
		super("ConflictError", 409, message || "Conflict");
		Object["setPrototypeOf"](this, ConflictError.prototype);
	}
}

/**
 * Represents a GONE error. The requested resource is no longer available at the server 
 * and no forwarding address is known. This condition is expected to be considered 
 * permanent. Clients with link editing capabilities SHOULD delete references t
 * o the Request-URI after user approval. If the server does not know, or has 
 * no facility to determine, whether or not the condition is permanent, the 
 * error 404 (NotFoundError) SHOULD be used instead. This response is 
 * cacheable unless indicated otherwise.
 */
export class GoneError extends HttpError {
	constructor(message?: string) {
		super("GoneError", 410, message || "Gone");
		Object["setPrototypeOf"](this, GoneError.prototype);
	}
}

/**
 * Represents an UNSUPPORTED MEDIA TYPE error. The server is refusing to service the request 
 * because the entity of the request is in a format not supported by the requested resource 
 * for the requested method.
 */
export class UnsupportedMediaTypeError extends HttpError {
	constructor(message?: string) {
		super("UnsupportedMediaTypeError", 415, message || "Unsupported Media Type");
		Object["setPrototypeOf"](this, UnsupportedMediaTypeError.prototype);
	}
}

/**
 * Represents an INTERNAL SERVER error. The server encountered an unexpected condition 
 * which prevented it from fulfilling the request.
 */
export class InternalServerError extends HttpError {
	constructor(message?: string) {
		super("InternalServerError", 500, message || "Internal Server Error");
		Object["setPrototypeOf"](this, InternalServerError.prototype);
	}
}

/**
 * Represents a NOT IMPLEMENTED error. The server does not support the functionality required
 *  to fulfill the request. This is the appropriate response when the server does not recognize 
 * the request method and is not capable of supporting it for any resource.
 */
export class NotImplementedError extends HttpError {
	constructor(message?: string) {
		super("NotImplementedError", 501, message|| "Not Implemented");
		Object["setPrototypeOf"](this, NotImplementedError.prototype);
	}
}

"use strict";

import * as express from "express"; 

/**
 * The supported HTTP methods.
 */
export enum HttpMethod {
	GET,
	POST,
	PUT,
	DELETE,
	HEAD,
	OPTIONS,
	PATCH
}

/**
 * Represents the current context of the request being handled.
 */
export class ServiceContext {
	/**
	 * The resolved language to be used in the current request handling. 
	 */
	language: string;
	/**
	 * The preferred media type to be used in the current request handling. 
	 */
	accept: string;
	/**
	 * The request object. 
	 */
	request: express.Request;
	/**
	 * The response object 
	 */
	response: express.Response; 
	/**
	 * The next function. It can be used to delegate to the next middleware
	 * registered the processing of the current request. 
	 */
	next: express.NextFunction;
}

/**
 * The Base class for all HTTP errors
 */
export abstract class HttpError extends Error {
  constructor(name: string, 
  			  public statusCode: number, 
  			  public message: string) {
    super(message);
    this.name = name;
  }
}

/**
 * Used to create a reference to a resource.
 */
export abstract class ReferencedResource {
	/**
	 * Constructor. Receives the location of the resource.
	 * @param location To be added to the Location header on response
	 * @param statusCode the response status code to be sent
	 */
	constructor(public location: string, public statusCode: number) {}
}

/**
 * The factory used to instantiate the object services
 */
export interface ServiceFactory {
	/**
	 * Create a new service object. Called before each request handling.
	 */
	create: (serviceClass: Function) => any,
	/**
	 * Return the type used to handle requests to the target service.
	 * By default, returns the serviceClass received, but you can use this
	 * to implement IoC integrations.
	 */
	getTargetClass: (serviceClass: Function) => FunctionConstructor;
}

"use strict";

import {ReferencedResource} from "./server-types"; 

/**
 * Inform that a new resource was created. Server will 
 * add a Location header and set status to 201
 */
export class NewResource extends ReferencedResource {
	/**
	 * Constructor. Receives the location of the new resource created.
	 * @param location To be added to the Location header on response
	 */
	constructor(location: string) {
		super(location, 201);
	}
}

/**
 * Inform that the request was accepted but is not completed.
 * A Location header should inform the location where the user
 * can monitor his request processing status.
 */
export class RequestAccepted extends ReferencedResource {
	/**
	 * Constructor. Receives the location where information about the 
	 * request processing can be found.
	 * @param location To be added to the Location header on response
	 */
	constructor(location: string) {
		super(location, 202);
	}
}

/**
 * Inform that the resource has permanently
 * moved to a new location, and that future references should use a
 * new URI with their requests.
 */
export class MovedPermanently extends ReferencedResource {
	/**
	 * Constructor. Receives the location where the resource can be found.
	 * @param location To be added to the Location header on response
	 */
	constructor(location: string) {
		super(location, 301);
	}
}

/**
 * Inform that the resource has temporarily
 * moved to another location, but that future references should
 * still use the original URI to access the resource.
 */
export class MovedTemporarily extends ReferencedResource {
	/**
	 * Constructor. Receives the location where the resource can be found.
	 * @param location To be added to the Location header on response
	 */
	constructor(location: string) {
		super(location, 302);
	}
}

/**
 * Used to download a resource.
 */
export class DownloadResource {
	/**
	 * Constructor. 
	 * @param filePath The file path to download.
	 * @param fileName The file name
	 */
	constructor(public filePath: string, public fileName: string) {}
}

/**
 * Used to download binary data as a file.
 */
export class DownloadBinaryData {
	/**
	 * Constructor. Receives the location of the resource.
	 * @param content The binary data to be downloaded as a file.
	 * @param mimeType The mime-type to be passed on Content-Type header.
	 * @param fileName The file name
	 */
	constructor(public content: Buffer, public mimeType: string, public fileName: string) {}
}

"use strict";

import * as express from "express";
import "multer"; 
import {InternalServer} from "./server-container"; 
import {HttpMethod, ServiceFactory} from "./server-types"; 

/**
 * The Http server main class. 
 */
export class Server {
	/**
	 * Create the routes for all classes decorated with our decorators
	 */
	static buildServices(router: express.Router, ...types) {
		let iternalServer: InternalServer = new InternalServer(router);
		iternalServer.buildServices(types);
	}

	/**
	 * Return all paths accepted by the Server
	 */
	static getPaths(): Array<string> {
		let result = new Array<string>();
		InternalServer.getPaths().forEach(value=>{
			result.push(value);
		});

		return result;
	}

	/**
	 * Register a custom serviceFactory. It will be used to instantiate the service Objects
	 * If You plan to use a custom serviceFactory, You must ensure to call this method before any typescript-rest service declaration.
	 */
	static registerServiceFactory(serviceFactory: ServiceFactory) {
		InternalServer.serviceFactory = serviceFactory;	
	}

	/**
	 * Configure the Server to use [typescript-ioc](https://github.com/thiagobustamante/typescript-ioc)
	 * to instantiate the service objects.
	 * If You plan to use IoC, You must ensure to call this method before any typescript-rest service declaration.
	 */
	static useIoC() {
		let ioc = require("typescript-ioc");
		Server.registerServiceFactory({
			create: (serviceClass) => {
				return ioc.Container.get(serviceClass);
			},
			getTargetClass: (serviceClass: Function) => {
				let typeConstructor: Function = serviceClass;
				if (typeConstructor['name']) {
					return <FunctionConstructor>typeConstructor;
				}
				while (typeConstructor = typeConstructor['__parent']) {
					if (typeConstructor['name']) {
						return <FunctionConstructor>typeConstructor;
					}
				}
				throw TypeError('Can not identify the base Type for requested target');
			}
		});
	}

	/**
	 * Return the set oh HTTP verbs configured for the given path
	 * @param path The path to search HTTP verbs
	 */
	static getHttpMethods(path: string): Array<HttpMethod> {
		let result = new Array<HttpMethod>();
		InternalServer.getHttpMethods(path).forEach(value=>{
			result.push(value);
		});

		return result;
	}

	/**
	 * A string used for signing cookies. This is optional and if not specified, 
	 * will not parse signed cookies.
	 * @param secret the secret used to sign
	 */
	static setCookiesSecret(secret: string) {
		InternalServer.cookiesSecret = secret;
	}

	/**
	 * Specifies a function that will be used to decode a cookie's value. 
	 * This function can be used to decode a previously-encoded cookie value 
	 * into a JavaScript string.
	 * The default function is the global decodeURIComponent, which will decode 
	 * any URL-encoded sequences into their byte representations.
	 * 
	 * NOTE: if an error is thrown from this function, the original, non-decoded 
	 * cookie value will be returned as the cookie's value.
	 * @param decoder The decoder function
	 */
	static setCookiesDecoder(decoder: (val: string) => string) {
		InternalServer.cookiesDecoder = decoder;
	}

	/**
	 * Set where to store the uploaded files
	 * @param dest Destination folder
	 */
	static setFileDest(dest: string) {
		InternalServer.fileDest = dest;
	}

	/**
	 * Set a Function to control which files are accepted to upload
	 * @param filter The filter function
	 */
	static setFileFilter(filter: (req: Express.Request, file: Express.Multer.File, 
					callback: (error: Error, acceptFile: boolean) => void) => void) {
		InternalServer.fileFilter = filter;
	}

	/**
	 * Set the limits of uploaded data
	 * @param limit The data limit
	 */
	static setFileLimits(limit: number) {
		InternalServer.fileLimits = limit;
	}
}

